        void cetak_semua() {
          try{
              String namaFile = "src/laporan/transaksi.jasper";
            Connection conn =DriverManager.getConnection("jdbc:mysql://localhost/Skripsiku", "root", "");
            
            HashMap parameter = new HashMap();
            File report_file = new File(namaFile);
            net.sf.jasperreports.engine.JasperReport jasperreport = (net.sf.jasperreports.engine.JasperReport) JRLoader.loadObject(report_file.getPath());
            JasperPrint jasperprint = JasperFillManager.fillReport(jasperreport, parameter, conn);
            JasperViewer.viewReport(jasperprint, false);
            JasperViewer.setDefaultLookAndFeelDecorated(true);
        }catch (Exception e){
        JOptionPane.showMessageDialog(null,e.getMessage());
        }
    
    
    }
    void cetak_satuan(){
            try{
            String namaFile = "src/laporan/transaksi1.jasper";
            Connection conn =DriverManager.getConnection("jdbc:mysql://localhost/skripsiku", "root", "");
            HashMap parameter = new HashMap();
            
            parameter.put("kode",kode.getText());
            parameter.put("kode1",kode1.getText());
            File report_file = new File(namaFile);
            net.sf.jasperreports.engine.JasperReport jasperreport = (net.sf.jasperreports.engine.JasperReport) JRLoader.loadObject(report_file.getPath());
            JasperPrint jasperprint = JasperFillManager.fillReport(jasperreport, parameter, conn);
            JasperViewer.viewReport(jasperprint, false);
            JasperViewer.setDefaultLookAndFeelDecorated(true);
        }catch (Exception e){
        JOptionPane.showMessageDialog(null,e.getMessage());
        }
    

    }


